!async function(){try{const t=await async function(){try{const t=await fetch("https://jsonplaceholder.typicode.com/posts");return await t.json()}catch(t){console.error("Error fetching data:",t)}}();if(t){console.log("Data fetched:",t),console.log("Processing data..."),processedData=function(t){return 0===t.length?null:t.reduce(((t,o)=>o.title.length>t.title.length?o:t))}(t),console.log("Processed data:",processedData),document.getElementById("apiData").textContent=processedData.title,console.log("Posting the processed data...");const o=await async function(t){try{const o=await fetch("https://jsonplaceholder.typicode.com/posts",{method:"POST",body:JSON.stringify({title:"Longest Title Found",body:`The longest title is: "${t.title}" (Post ID: ${t.id})`,userId:1}),headers:{"Content-type":"application/json; charset=UTF-8"}});return await o.json()}catch(t){throw console.error("Error posting data:",t),t}}(processedData);console.log("Post result:",o)}}catch(t){console.log("Error:",t)}}();